import { useState, useRef } from 'react'
import PropTypes from 'prop-types'
import Togglable from './Togglable'
import NewBlogForm from './NewBlogForm'

const BlogDetails = ({ blog, visible, likeBlog, removeBlog, own }) => {
    if (!visible) return null

    const addedBy = blog.user && blog.user.name ? blog.user.name : 'anonymous'

    return (
        <div>
            <div>
                <a href={blog.url}>{blog.url}</a>
            </div>
            <div>
                {blog.likes} likes{' '}
                <button onClick={() => likeBlog(blog.id)}>like</button>
            </div>
            {addedBy}
            {own && <button onClick={() => removeBlog(blog.id)}>remove</button>}
        </div>
    )
}

const Blog = ({ blog, likeBlog, removeBlog, user }) => {
    const [visible, setVisible] = useState(false)

    const style = {
        padding: 3,
        margin: 5,
        borderStyle: 'solid',
        borderWidth: 1,
    }

    return (
        <div style={style} className="blog">
            {blog.title} {blog.author}
            <button onClick={() => setVisible(!visible)}>
                {visible ? 'hide' : 'view'}
            </button>
            <BlogDetails
                blog={blog}
                visible={visible}
                likeBlog={likeBlog}
                removeBlog={removeBlog}
                own={blog.user && user.username === blog.user.username}
            />
        </div>
    )
}

const Bloglist = ({ blogs, likeBlog, removeBlog, user, createBlog }) => {
    const blogFormRef = useRef()
    return (
        <div>
            <div>
                <Togglable buttonLabel="new blog" ref={blogFormRef}>
                    <NewBlogForm onCreate={createBlog} />
                </Togglable>
            </div>
            <div id="blogs">
                {blogs.map((blog) => (
                    <Blog
                        key={blog.id}
                        blog={blog}
                        likeBlog={likeBlog}
                        removeBlog={removeBlog}
                        user={user}
                    />
                ))}
            </div>
        </div>
    )
}

Blog.propTypes = {
    blog: PropTypes.shape({
        title: PropTypes.string.isRequired,
        author: PropTypes.string.isRequired,
        url: PropTypes.string.isRequired,
        likes: PropTypes.number.isRequired,
        user: PropTypes.shape({
            username: PropTypes.string.isRequired,
            name: PropTypes.string.isRequired,
        }),
    }).isRequired,
    user: PropTypes.shape({
        username: PropTypes.string.isRequired,
    }),
    likeBlog: PropTypes.func.isRequired,
    removeBlog: PropTypes.func.isRequired,
}

export default Bloglist
